import{_ as a,c as e,o as t,a4 as r}from"./chunks/framework.4aTu-Nia.js";const i="/assets/bs.Byos6_so.png",l="/assets/maven.CfTv9f6I.png",f=JSON.parse('{"title":"P1 环境准备","description":"","frontmatter":{},"headers":[],"relativePath":"RD/java/spring/liulaoshi/01_env.md","filePath":"RD/java/spring/liulaoshi/01_env.md"}'),o={name:"RD/java/spring/liulaoshi/01_env.md"},s=r('<h1 id="p1-环境准备" tabindex="-1">P1 环境准备 <a class="header-anchor" href="#p1-环境准备" aria-label="Permalink to &quot;P1 环境准备&quot;">​</a></h1><h2 id="内容介绍" tabindex="-1">内容介绍 <a class="header-anchor" href="#内容介绍" aria-label="Permalink to &quot;内容介绍&quot;">​</a></h2><ul><li><p>java EE 企业级框架：spring boot + MybatisPlus</p></li><li><p>前端框架：Vue 3 + ElementUi</p></li><li><p>部署打包</p></li></ul><h2 id="课程内容" tabindex="-1">课程内容 <a class="header-anchor" href="#课程内容" aria-label="Permalink to &quot;课程内容&quot;">​</a></h2><h2 id="概念介绍" tabindex="-1">概念介绍 <a class="header-anchor" href="#概念介绍" aria-label="Permalink to &quot;概念介绍&quot;">​</a></h2><h3 id="web技术基础" tabindex="-1">Web技术基础 <a class="header-anchor" href="#web技术基础" aria-label="Permalink to &quot;Web技术基础&quot;">​</a></h3><p>目前市面上的软件主要可分为两种:</p><p>BS:(Browser/Server浏览器/服务器架构模式)</p><p>CS:(Client/Server客户端/服务器架构模式)。</p><h3 id="架构对比" tabindex="-1">架构对比 <a class="header-anchor" href="#架构对比" aria-label="Permalink to &quot;架构对比&quot;">​</a></h3><ul><li><p>C/S架构主要特点是交互性强，具有安全访问模式，网络流量低，响应速度快因为客户端负责大多数业务逻辑和UI演示，所以也被称为胖客户端，C/S结构的软件需要针对不同的操作系统开发不同版本的软件。</p></li><li><p>随着互联网的兴起，CS架构不适合Web，最大的原因是Web应用程序的修改和升级非常迅速，而CS架构需要每个客户端逐个升级桌面App，因此，Browser/Server模式开始流行，简称BS架构。</p></li><li><p>B/S架构的主要特点是分散性高、维护方便、开发简单、共享性高、总拥有成本低</p></li></ul><h3 id="bs架构原理" tabindex="-1">BS架构原理 <a class="header-anchor" href="#bs架构原理" aria-label="Permalink to &quot;BS架构原理&quot;">​</a></h3><p>在BS架构下，客户端只需要浏览器，应用程序的逻辑和数据都存储在服务器端浏览器只需要请求服务器，获取Web页面，并把Web页面展示给用户即可。</p><p><img src="'+i+'" alt="Alt text" data-fancybox="gallery"></p><h2 id="开发配置" tabindex="-1">开发配置 <a class="header-anchor" href="#开发配置" aria-label="Permalink to &quot;开发配置&quot;">​</a></h2><p>java环境配置：1.8</p><p>开发工具：IDEA</p><p>项目管理工具：Maven</p><p>自动化构建和依赖管理</p><p><img src="'+l+'" alt="Alt text" data-fancybox="gallery"></p>',20),n=[s];function p(h,c,d,_,b,u){return t(),e("div",null,n)}const S=a(o,[["render",p]]);export{f as __pageData,S as default};
