import{_ as l,c as i,o as a,a4 as t}from"./chunks/framework.4aTu-Nia.js";const e="/assets/image-2.BgSDPOQ9.png",s="/assets/image-3.BuwewXsU.png",o="/assets/a2edb9aca13502eb8d362350343afa8.CA4GxkT6.jpg",c="/assets/b7f1fa8dd6d7b1dfbf8fa3bdf08d0ab.DjP4ZAyG.png",r="/assets/image-1.C8zW2_6A.png",k=JSON.parse('{"title":"485 倾角仪","description":"","frontmatter":{},"headers":[],"relativePath":"work/ysb/485inclinometer/index.md","filePath":"work/ysb/485inclinometer/index.md"}'),n={name:"work/ysb/485inclinometer/index.md"},d=t('<h1 id="_485-倾角仪" tabindex="-1">485 倾角仪 <a class="header-anchor" href="#_485-倾角仪" aria-label="Permalink to &quot;485 倾角仪&quot;">​</a></h1><h2 id="产品信息" tabindex="-1">产品信息 <a class="header-anchor" href="#产品信息" aria-label="Permalink to &quot;产品信息&quot;">​</a></h2><p><img src="'+e+'" alt="alt text" data-fancybox="gallery"></p><img src="'+s+'" width="300" height="200"><br><img src="'+o+'" width="300" height="200"><p>连线</p><ul><li>红：VCC</li><li>黑：GND</li><li>绿：485A</li><li>白：485B</li></ul><p>范围：±30°</p><h2 id="通信协议" tabindex="-1">通信协议 <a class="header-anchor" href="#通信协议" aria-label="Permalink to &quot;通信协议&quot;">​</a></h2><h3 id="读取采集数据格式" tabindex="-1">读取采集数据格式 <a class="header-anchor" href="#读取采集数据格式" aria-label="Permalink to &quot;读取采集数据格式&quot;">​</a></h3><p><img src="'+c+'" alt="alt text" data-fancybox="gallery"></p><div class="info custom-block"><p class="custom-block-title">PC 发送：64 00 D7 EF 02 00 00 11 00 00 08 00 F8 D3 (先发低位再发高位)</p><ul><li>64 00:仪器地址（2 字节）,此处地址为 100 （或者为 00 00 ）注：仪器地址写 00 00 时所有已连接仪器都会回复</li><li>D7 EF:包头 （固定）</li><li>02 00:命令字</li><li>00 11 00 00 :内存地址，即 4352</li><li>08 00 :请求读取的数据内容长度 即表示需要读取 8 个字节</li><li>F8 D3:万能校验码（固定）</li></ul></div><div class="info custom-block"><p class="custom-block-title">仪器回复（先发低位再发高位）：</p><p>64 00 1A D7 EF 03 00 00 11 00 00 08 00 +crc1（2 个字节）+x 轴角度（4 个字节，单位为度）+y 轴角度（4 个字节，单位为度）+crc2（2 个字节）</p><ul><li>64 00 D7 EF：仪器地址（2 字节）+ D7 EF（固定，2 字节)</li><li>03 00：命令字(2 字节)</li><li>00 11 00 00：内存地址：4352 （4 字节）</li><li>08 00：数据长度（8 字节）表示 2 个 float 数据的长度</li><li>crc 校验 1(2 字节) :从命令字开始算起（即 03 开始）</li><li>数据内容(8 个字节)：</li><li>crc2(2 字节)，从数据内容开始算起</li></ul></div><h3 id="读取斜率补偿系数" tabindex="-1">读取斜率补偿系数 <a class="header-anchor" href="#读取斜率补偿系数" aria-label="Permalink to &quot;读取斜率补偿系数&quot;">​</a></h3><div class="info custom-block"><p class="custom-block-title">PC 发送：00 00 D7 EF 02 00 18 00 00 00 04 00 F8 D3</p><ul><li>00 00:仪器地址（2 字节）（或者为 00 00 ）注：仪器地址写 00 00 时所有已连接仪器都会回复</li><li>D7 EF:包头 （固定）</li><li>02 00:命令字</li><li>18 00 00 00 :内存地址，即 24</li><li>04 00 :请求读取的数据内容长度 即表示需要读取 4 个字节</li><li>F8 D3:万能校验码（固定）</li></ul></div><div class="info custom-block"><p class="custom-block-title">仪器回复：</p><p>64 00 D7 EF 03 00 18 00 00 00 04 00 CRC1 校验（2 个字节） 53 05 B3 3E +crc2 校验（2 个字节）</p><ul><li>64 00 D7 EF：仪器地址（2 字节）+ D7 EF（固定，2 字节)</li><li>03 00：命令字(2 字节)</li><li>18 00 00 00：内存地址（此处为 24）</li><li>04 00：数据长度（2 字节），4</li><li>01 06：CRC1 校验，从命令字开始算起</li><li>53 05 B3 3E：数据内容，即斜率补偿系数</li><li>CRC2 校验，从数据内容开始算起</li></ul></div><h3 id="读取零点偏移补偿系数" tabindex="-1">读取零点偏移补偿系数 <a class="header-anchor" href="#读取零点偏移补偿系数" aria-label="Permalink to &quot;读取零点偏移补偿系数&quot;">​</a></h3><div class="info custom-block"><p class="custom-block-title">PC 发送：00 00 D7 EF 02 00 1C 00 00 00 04 00 F8 D3</p><ul><li>00 00:仪器地址（2 字节）（或者为 00 00 ）注：仪器地址写 00 00 时所有已连接仪器都会回复</li><li>D7 EF:包头 （固定）</li><li>02 00:命令字</li><li>1C 00 00 00 :内存地址，即 28</li><li>04 00 :请求读取的数据内容长度 即表示需要读取 4 个字节</li><li>F8 D3:万能校验码（固定）</li></ul></div><div class="info custom-block"><p class="custom-block-title">仪器回复：</p><p>64 00 D7 EF 03 00 1C 00 00 00 04 00 CRC1 校验（2 个字节） 00 00 00 00 CRC2 校验（2 个字节）</p><ul><li>64 00 D7 EF：仪器地址（2 字节）+ D7 EF（固定，2 字节)</li><li>03 00：命令字(2 字节)</li><li>1C 00 00 00：内存地址（此处为 28）</li><li>04 00：数据长度（2 字节），4</li><li>00 82：CRC1 校验，从命令字开始算起</li><li>00 00 00 00：数据内容，即零点偏移补偿系数</li><li>00 24：CRC2 校验，从数据内容开始算起</li></ul></div><h2 id="电路改进" tabindex="-1">电路改进 <a class="header-anchor" href="#电路改进" aria-label="Permalink to &quot;电路改进&quot;">​</a></h2><p>（1）所需物料</p><ul><li>220μF 25V</li><li>电解电容 2 个</li></ul><p>（2）改进说明</p><p>如下图所示在 U2 电源输入端和 U6 电源输入端分别焊接一个 220μF 25V 电解电容 1 个，注意电容极性。</p><p><img src="'+r+'" alt="alt text" data-fancybox="gallery"></p><p>（3）接线说明</p><ul><li>红色接电源正极 VCC（DC9-36V）</li><li>屏蔽线套热缩管与黑色线接 GND</li><li>绿色线接 485A</li><li>白色线接 485B</li></ul>',28),p=[d];function u(h,m,b,_,f,C){return a(),i("div",null,p)}const F=l(n,[["render",u]]);export{k as __pageData,F as default};
